import Swal from 'sweetalert2';
export const SWEET_ALERT = (alert_title, alert_text, alert_type, alert_icon, confBtn = 'OK', cancelBtn, html, cancelText) => {
  const options = {
    title: alert_title,
    type: alert_type,
    icon: alert_icon,
    showCancelButton: !!cancelBtn,
    reverseButtons: true,
    confirmButtonClass: 'app_button secondary_btn',
    confirmButtonText: confBtn,
    cancelButtonClass: cancelBtn ? 'app_button cancel_btn' : '',
    cancelButtonText: cancelText
  };
  options[html ? 'html' : 'text'] = alert_text;
  return Swal.fire(options);
};
